//Description : Function that get at once all data in the recap page of a spreadsheet. 
//All data are transformed into a csv string in order to get those data all at once in javascript.
//this permits to minimize access on server side and improve drastically access speed of datas.
//Return : a csv string that contains all the recap data.
function GetRecapData(spreadsheet)
{ 
  var ss = SpreadsheetApp.openByUrl(spreadsheet);
  var sheet = ss.getSheets()[0];

  recaprange = sheet.getRange("C2:I23");

  var dataarray = recaprange.getValues();

  var todaydatastring = dataarray[2][0] + ";" + dataarray[2][1] + ";" + dataarray[2][2];
  var laststagedatastring = dataarray[0][6];
  var nextstagedatastring = dataarray[2][6] + ";" + dataarray[3][6] + ";" + Utilities.formatDate(dataarray[4][6], ss.getSpreadsheetTimeZone(), "dd/MM/yyyy");
  var startdatastring = dataarray[6][6] + ";" + Utilities.formatDate(dataarray[8][6], ss.getSpreadsheetTimeZone(), "dd/MM/yyyy") + ";" + Utilities.formatDate(dataarray[9][6], ss.getSpreadsheetTimeZone(), "dd/MM/yyyy");
  var latlongdatastring = dataarray[11][6] + ";" + dataarray[12][6];
  var prevlatlongdatastring = dataarray[13][6] + ";" + dataarray[14][6];

  var altitude = Maps.newElevationSampler().sampleLocation(dataarray[11][6], dataarray[12][6]);
  altitude = altitude.results[0].elevation;
  //var altitude = 0;

  var destinationnumberstring = dataarray[1][6];

  var recapdatastring = todaydatastring + ";" + laststagedatastring + ";" + nextstagedatastring + ";" + startdatastring + ";" + latlongdatastring + ";" + altitude;

  //put into string monthlydata
  var i =0;
  var j =0;
  var monthlydata = "";
  for(i = 0 ; i< 12 ; i++)
  {
    for(j = 0 ; j <3 ; j++)
    {
      monthlydata = monthlydata + dataarray[i+10][j] + ";" ;
    }
  }

  recapdatastring = recapdatastring + ";" + monthlydata + destinationnumberstring + ";" + prevlatlongdatastring; // already ;

  return recapdatastring;
}

//Description : Find the current week number of the challenge we are currently in.
function GetCurrentWeek(spreadsheet)
{
  var ss = SpreadsheetApp.openByUrl(spreadsheet);
  var currentDate = new Date();

  var Found = false;
  var week = 2;

  var sheet = ss.getSheets()[2];

  while((!Found)&&(week<=128))
  {
    //Get Week Start
    var weekstring = week + ""; 
    var cellstartweek = "B"+weekstring;
    var cellendweek = "C"+weekstring;

    var temprange = "A"+weekstring+":F"+weekstring;

    weekrange = sheet.getRange(temprange);

    var cell1 = weekrange.getCell(1, 2);
    var cell2 = weekrange.getCell(1, 3);

    if((currentDate>=cell1.getValue()) && (currentDate<=cell2.getValue()))
    {
      Found = true;
    }
    else
    {
      Found = false;
    }
    week = week+1
  }

  week = week-2;

  return week;
}

//Description : Get at once the data for a specified week of the challenge
//using cache for efficiency
//Return : a csv string that contains all week datas.
function GetWeekData2(number, spreadsheet)
{  
  var weeksheetdata = "";
  var ss = SpreadsheetApp.openByUrl(spreadsheet);

  //check if data in cache
  var cache = CacheService.getPublicCache();
  var cached = cache.get("weeksheetcontent");
  if (cached == null) {
    //get content

    var sheet = ss.getSheets()[2]; 
    weeksheetdata = sheet.getRange("A2:F128").getValues();
    cache.put("weeksheetcontent", weeksheetdata, 1500); // cache for 25 minutes
    number= number-1;
  }
  else
  {
    //get string comma separated
    weeksheetdata = cached;
    //transform to array
    var weeksheetarray1dim = weeksheetdata.split(",");
    var weeksheetarray2dim = [];
    var j=0;
    var k=0;

    weeksheetarray2dim[j]=[];

    for(var i=0 ;i<128 ; i++)
    {
      if(i%6 == 0)
      {
        j++;
        weeksheetarray2dim[j]=[];
        k=0;
      }
      else
      {
        k++;
      }
      weeksheetarray2dim[j][k]=weeksheetarray1dim[i];
    }
    weeksheetdata = weeksheetarray2dim;
  }

  //get today's date
  var currentDate = new Date();

  //Get Week Start
  var week = number;

  var weekdataarray = weeksheetdata[week];

  //Logger.log(weeksheetdata);

  //Format week dates
  var weeklabel = "";

  var tempstartdate =new Date(weekdataarray[1]);
  var tempenddate =new Date(weekdataarray[2]);


  if((currentDate>=tempstartdate) && (currentDate<=tempenddate))
  {
    weeklabel = "Current week";
  }
  else
  {
    var formatteddatestartweek = Utilities.formatDate(tempstartdate, ss.getSpreadsheetTimeZone(), "dd/MM/yyyy");  
    var formatteddateendweek = Utilities.formatDate(tempenddate, ss.getSpreadsheetTimeZone(), "dd/MM/yyyy");
    weeklabel = "From "+formatteddatestartweek+" to "+formatteddateendweek;
  }

  //create output string of data
  var weekdatastring = weekdataarray[0]+";"+weeklabel+";"+(Math.round(weekdataarray[3]*100)/100)+";"+(Math.round(weekdataarray[4]*100)/100)+";"+(Math.round(weekdataarray[5]*100)/100);

  return weekdatastring;
}

//Description : add in a spreadsheet a new data log
function LogData(cyclingdata, walkingdata, swimmingdata, spreadsheet)
{
  var ss = SpreadsheetApp.openByUrl(spreadsheet);
  var sheet = ss.getSheets()[4];
  var rowcontents = [new Date(),parseFloat(cyclingdata),parseFloat(walkingdata)];

  sheet.appendRow(rowcontents);

  //Clear current cache
  ClearCache();
}

//Description : Clear the cached data for all weeks.
function ClearCache()
{
  //clearcache
  var cache = CacheService.getPublicCache();
  cache.remove("weeksheetcontent");
}

